const fs = require("fs-extra");
const path = require("path");

// Copy assets from jsx-slack into current directory
const dir = path.resolve(require.resolve("jsx-slack"), "../../");
const { files } = require(path.resolve(dir, "package.json"));

for (const f of files) {
  fs.removeSync(path.resolve(__dirname, f));
  fs.copySync(path.resolve(dir, f), path.resolve(__dirname, f));
}
